import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View, StatusBar, TouchableOpacity, Image } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getAuth } from 'firebase/auth';
import { getFirestore, doc, getDoc } from 'firebase/firestore';

const Profile = () => {
  const navigation = useNavigation();
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [rol, setRol] = useState('');
  const [carrera, setCarrera] = useState('');
  const [semestre, setSemestre] = useState('');
  const [numeroControl, setNumeroControl] = useState('');
  const [turno, setTurno] = useState('');

  useEffect(() => {
    fetchUserInfo();
  }, []);

  const fetchUserInfo = async () => {
    try {
      const db = getFirestore();
      const auth = getAuth();

      const user = auth.currentUser;
      const userDocRef = doc(db, 'users', user.uid); // Utiliza user.uid como el ID del usuario actual

      const userDoc = await getDoc(userDocRef);

      if (userDoc.exists()) {
        const userData = userDoc.data();

        setNombre(userData.nombre);
        setCorreo(userData.correo);
        setRol(userData.rol);
        setCarrera(userData.carrera);
        setSemestre(userData.semestre);
        setNumeroControl(userData.numeroControl);
        setTurno(userData.turno);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  return (
    <View style={styles.container}>
      <StatusBar backgroundColor="#0069c0" barStyle="light-content" />

      <View style={styles.userContainer}>
        <Image source={require('../images/usuario.png')} style={styles.userIcon} />
      </View>

      <View style={styles.infoContainer}>
        <Text style={styles.infoText}>Nombre: {nombre}</Text>
        <Text style={styles.infoText}>Correo: {correo}</Text>
        <Text style={styles.infoText}>Rol: {rol}</Text>
        <Text style={styles.infoText}>Carrera: {carrera}</Text>
        <Text style={styles.infoText}>Semestre: {semestre}</Text>
        <Text style={styles.infoText}>Numero de Control: {numeroControl}</Text>
        <Text style={styles.infoText}>Turno: {turno}</Text>
      </View>

      <TouchableOpacity style={styles.editButton} onPress={() => navigation.navigate('EditInfo')}>
        <Text style={styles.editButtonText}>Editar informaci√≥n</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  userContainer: {
    marginTop: 50,
    marginBottom: 30,
    alignItems: 'center',
    justifyContent: 'center',
  },
  userIcon: {
    width: 120,
    height: 120,
    resizeMode: 'contain',
  },
  infoContainer: {
    marginBottom: 30,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  infoText: {
    fontSize: 18,
    marginVertical: 5,
  },
  editButton: {
    backgroundColor: '#0069c0',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  editButtonText: {
    color: '#fff',
    fontSize: 16,
  },
});

export default Profile;
