import React from "react";
import { NavigationContainer, useNavigation  } from '@react-navigation/native';
import { createDrawerNavigator, DrawerContentScrollView, DrawerItemList  } from "@react-navigation/drawer";
import { createStackNavigator } from '@react-navigation/stack';
import { Text, Image, View } from 'react-native';
import { Divider, NativeBaseProvider } from "native-base";
import Categorías from "../screens/Categorías";
import SettingScreen from "../screens/SettingScreen";
import SeguridadScreen from "../screens/SeguridadScreen";
import Notificación from "../screens/Notificación";
import idioma from "../screens/idioma";
import Tema from "../screens/Tema";
import Profile from "../screens/Profile";
import AgregarAnuncio from "../screens/AgregarAnuncio";
import Splash from '../screens/Splash';
import Login from '../screens/Login';
import VerTuActividad from '../screens/VerTuActividad';
import ChangePassword from '../screens/ChangePassword';
import SuspenderMiCuenta from '../screens/SuspenderMiCuenta';
import Register from '../screens/Register';
import ForgotPassword from '../screens/ForgotPassword';
import EditInfo from '../screens/EditInfo';
import SobreNosotros from '../screens/SobreNosotros';
import Contacto from '../screens/Contacto';


const Drawer = createDrawerNavigator();
const Stack = createStackNavigator(); // Importa el StackNavigator


function AuthStack() {
    return (
<Stack.Navigator headerMode="none">
      <Stack.Screen name="Splash" component={Splash} />
      <Stack.Screen name="Login" component={Login} />
      <Stack.Screen name="Register" component={Register} />
      <Stack.Screen name="ForgotPassword" component={ForgotPassword} />
    </Stack.Navigator>
  );
}

function CustomDrawerContent(props) {
  const navigation = useNavigation();

  const goToScreen = (screenName) => {
    navigation.navigate(screenName);
  };

  return (
    <DrawerContentScrollView {...props}>
      <View style={{ padding: 16 }}>
        <Image source={require('../images/usuario.png')} style={{ width: 150, height: 150 }} />
        <Text style={{ fontSize: 24, fontWeight: 'bold', marginTop: 16 }}>David Aranda</Text>
        <Text style={{ fontSize: 16, color: "gray", marginTop: 16 }}>1915157</Text>
      </View>
      <Divider />
      <DrawerItemList
        {...props}
        onItemPress={({ route }) => goToScreen(route.name)}
      />
    </DrawerContentScrollView>
  );
}

export function Conjunto() {
  return (
    <NavigationContainer>
      <NativeBaseProvider>
        <Drawer.Navigator drawerContent={(props) => <CustomDrawerContent {...props} />}>
          <Drawer.Screen name="Splash" component={Splash} />
          <Drawer.Screen name="Login" component={Login} />
          <Drawer.Screen name="Register" component={Register} />
          <Drawer.Screen name="ForgotPassword" component={ForgotPassword} />
          <Drawer.Screen name="Categorías" component={Categorías} />
          <Drawer.Screen name="Profile" component={Profile} />
          <Drawer.Screen name="EditInfo" component={EditInfo} />
          <Drawer.Screen name="AgregarAnuncio" component={AgregarAnuncio} />
          <Drawer.Screen name="Notificación" component={Notificación} />
          <Drawer.Screen name="SobreNosotros" component={SobreNosotros} />
          <Drawer.Screen name="Contacto" component={Contacto} />
          <Drawer.Screen name="SettingScreen" component={SettingScreen} />
          <Drawer.Screen name="idioma" component={idioma} />
          <Drawer.Screen name="Tema" component={Tema} />
          <Drawer.Screen name="SeguridadScreen" component={SeguridadScreen} />
          <Drawer.Screen name="VerTuActividad" component={VerTuActividad} />
          <Drawer.Screen name="ChangePassword" component={ChangePassword} />
          <Drawer.Screen name="SuspenderMiCuenta" component={SuspenderMiCuenta} />
        </Drawer.Navigator>
      </NativeBaseProvider>
    </NavigationContainer>
  );
}
