//Cambiar tema
import React, { useState, StyleSheet, TouchableOpacity  } from "react";

import {
  NativeBaseProvider,
  Center,
  Box,
  FormControl,
  Stack,
  Text,
  Button,
  HStack,
  Radio,
  VStack,
} from "native-base";

const ThemeSettingsScreen = ({ navigation }) => {
  const [selectedValue, setSelectedValue] = useState("light");

  const handleChange = (value) => {
    setSelectedValue(value);
  };

  return (
    <NativeBaseProvider>
      <Center flex={1}>
        <Box maxWidth="90%" mx="auto">
          <FormControl>
            <Stack space={4} alignItems="center">
              <Text fontSize="xl">Tema:</Text>
              <VStack space={4} alignItems="flex-start">
                <Radio.Group
                  name="theme"
                  value={selectedValue}
                  onChange={handleChange}
                >
                  <VStack space={3}>
                    <Radio value="light">Claro</Radio>
                    <Radio value="dark">Oscuro</Radio>
                  </VStack>
                </Radio.Group>
              </VStack>
              
            </Stack>
          </FormControl>
        </Box>
      </Center>
    </NativeBaseProvider>
  );
};

export default ThemeSettingsScreen;
