import React from "react";
import { NavigationContainer } from '@react-navigation/native';
import { createDrawerNavigator, DrawerContentScrollView, DrawerItemList  } from "@react-navigation/drawer";
import { createStackNavigator } from '@react-navigation/stack';
import { Text, Image, View } from 'react-native';
import { Divider, NativeBaseProvider } from "native-base";
import Categorías from "../screens/Categorías";
import SettingScreen from "../screens/SettingScreen";
import SeguridadScreen from "../screens/SeguridadScreen";
import Notificación from "../screens/Notificación";
import idioma from "../screens/idioma";
import Tema from "../screens/Tema";
import Profile from "../screens/Profile";
import AgregarAnuncio from "../screens/AgregarAnuncio";
import Splash from '../screens/Splash';
import Login from '../screens/Login';
import VerTuActividad from '../screens/VerTuActividad';
import ChangePassword from '../screens/ChangePassword';
import SuspenderMiCuenta from '../screens/SuspenderMiCuenta';
import Register from '../screens/Register';
import ForgotPassword from '../screens/ForgotPassword';
import EditInfo from '../screens/EditInfo';
import SobreNosotros from '../screens/SobreNosotros';
import Contacto from '../screens/Contacto';


const Drawer = createDrawerNavigator();
const Stack = createStackNavigator(); // Importa el StackNavigator


const PerfilStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Perfil" component={Profile} />
      <Stack.Screen name="EditarInformacion" component={EditInfo} />
    </Stack.Navigator>
  );
};

const SettingStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Setting" component={SettingScreen} />
      <Stack.Screen name="idioma" component={idioma} />
      <Stack.Screen name="Tema" component={Tema} />
      <Stack.Screen name="Seguridad" component={SeguridadScreen} />
      <Stack.Screen name="Actividad" component={VerTuActividad} />
      <Stack.Screen name="Cambiar contraseña" component={ChangePassword} />
    </Stack.Navigator>
  );
};

const HomeStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Home" component={Categorías} />
    </Stack.Navigator>
  );
};

const PublicarStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Publicar" component={AgregarAnuncio} />
    </Stack.Navigator>
  );
};

const NotificacionesStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Notificación" component={Notificación} />
    </Stack.Navigator>
  );
};

const NosotrosStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="SobreNosotros" component={SobreNosotros} />
      <Stack.Screen name="Contacto" component={Contacto} />
    </Stack.Navigator>
  );
};

const SuspenderStack = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="SuspenderMiCuenta" component={SuspenderMiCuenta} />
    </Stack.Navigator>
  );
};

const DrawerNavigation = () => {
  return (
    <Drawer.Navigator initialRouteName="Home">
      <Drawer.Screen name="Home" component={HomeStack} />
      <Drawer.Screen name="Mi perfil" component={PerfilStack} />
      <Drawer.Screen name="Publicar" component={PublicarStack} />
      <Drawer.Screen name="Notificaciones" component={NotificacionesStack} />
      <Drawer.Screen name="Sobre Nosotros" component={HomeStack} />
      <Drawer.Screen name="Configuracion" component={SettingStack} />
      <Drawer.Screen name="Suspender mi Cuenta" component={SuspenderStack} />
    </Drawer.Navigator>
  );
};

export default DrawerNavigation;
