import React, { useState } from 'react';
import { View, Text, TextInput, Image, TouchableOpacity, StyleSheet, Picker } from 'react-native';
import firestore from '@react-native-firebase/firestore';
import firebaseApp from '../firebase/credenciales'; // Importa las credenciales de Firebase desde el archivo credenciales.js


const Registro = ({ navigation }) => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [aceptarTerminos, setAceptarTerminos] = useState(false);
  const [rol, setRol] = useState(''); // Estado para almacenar el valor seleccionado del picker

  const handleRegistro = async () => {
    try {
      const userCredential = await auth().createUserWithEmailAndPassword(email, password);
      const user = userCredential.user;
  
      // Almacenar datos adicionales en Firestore
      await firestore()
        .collection('usuarios') // Nombre de la colección en Firestore
        .doc(user.uid) // Utilizar el ID del usuario como ID del documento
        .set({
          nombre: nombre,
          rol: rol,
          // Otros campos que desees almacenar
        });
  
      console.log('Usuario registrado:', user);
    } catch (error) {
      console.log('Error al registrar el usuario:', error);
    }
  };  

  return (
    <View style={styles.container}>
      <Image
        source={require('../images/logo.jpg')}
        style={styles.logo}
      />
      <TextInput
        style={styles.input}
        onChangeText={setNombre}
        value={nombre}
        placeholder="Nombre"
      />
      <TextInput
        style={styles.input}
        onChangeText={setEmail}
        value={email}
        placeholder="Correo"
        keyboardType="email-address"
      />
      <TextInput
        style={styles.input}
        onChangeText={setPassword}
        value={password}
        placeholder="Contraseña"
        secureTextEntry={true}
      />

    {/* Agregar el picker */}
    <View style={styles.pickerContainer}>
        <Picker
          selectedValue={rol}
          onValueChange={(itemValue) => setRol(itemValue)}
          style={styles.picker}
          itemStyle={styles.pickerItem}
        >
          <Picker.Item label="Selecciona un rol" value="" />
          <Picker.Item label="Maestro" value="maestro" />
          <Picker.Item label="Alumno" value="alumno" />
        </Picker>
      </View>

      <View style={styles.checkboxContainer}>
        <TouchableOpacity
          style={styles.checkbox}
          onPress={() => setAceptarTerminos(!aceptarTerminos)}
        >
          {aceptarTerminos ? (
            <Text style={styles.checkmark}>&#10003;</Text>
          ) : null}
        </TouchableOpacity>
        <Text style={styles.label}>Acepto los Términos y Condiciones</Text>
      </View>
      
      <TouchableOpacity
        style={[styles.button, !aceptarTerminos && styles.disabledButton]}
        onPress={handleRegistro}
        disabled={!aceptarTerminos}
      >
        <Text style={styles.buttonText}>Confirmar</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.goBack()}
      >
        <Text style={styles.buttonText} >Regresar</Text>
      </TouchableOpacity>
    </View>
  );
}

export default Registro;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#fff',
  },
  logo: {
    width: 150,
    height: 150,
    marginBottom: 30,
  },
  input: {
    width: '80%',
    height: 50,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    paddingLeft: 10,
    marginBottom: 20,
  },
  checkboxContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  pickerContainer: {
    width: '80%',
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 20,
    borderRadius: 5,
    overflow: 'hidden',
  },
  picker: {
    height: 40,
    color: 'black',
  },
  pickerItem: {
    fontSize: 15,
    color: 'black',
  },
  checkbox: {
    width: 20,
    height: 20,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 3,
    marginRight: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  checkmark: {
    fontSize: 16,
    color: '#fff',
  },
  label: {
    fontSize: 16,
    color: '#333',
  },
  button: {
    width: '80%',
    height: 50,
    backgroundColor: '#007aff',
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'center',
  },
  disabledButton: {
    backgroundColor: '#ccc',
  },
  buttonText: {
    fontSize: 16,
    color: '#fff',
  },
});
