import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View, StatusBar, TouchableOpacity, Image } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getFirestore, doc, getDoc } from 'firebase/firestore';

const Profile = () => {
  const navigation = useNavigation();
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [rol, setRol] = useState('');
  const [carrera, setCarrera] = useState('');
  const [semestre, setSemestre] = useState('');
  const [numeroControl, setNumeroControl] = useState('');
  const [turno, setTurno] = useState('');

  const fetchUserInfo = async () => {
    try {
      const db = getFirestore();
      const userDocRef = doc(db, 'users', 'YOUR_USER_ID'); // Reemplaza 'YOUR_USER_ID' por el ID del usuario actual

      const userDoc = await getDoc(userDocRef);

      if (userDoc.exists()) {
        const userData = userDoc.data();

        setNombre(userData.nombre);
        setCorreo(userData.correo);
        setRol(userData.rol);
        setCarrera(userData.carrera);
        setSemestre(userData.semestre);
        setNumeroControl(userData.numeroControl);
        setTurno(userData.turno);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <View style={styles.container}>
      <StatusBar backgroundColor="#0069c0" barStyle="light-content" />

      <View style={styles.userContainer}>
        <Image source={require('../images/usuario.png')} style={styles.userIcon} />
      </View>

      <View style={styles.infoContainer}>
        <Text style={styles.infoText}>Nombre:</Text>
        <Text style={[styles.infoText, styles.bold]}>{nombre}</Text>
        <Text style={styles.infoText}>Correo:</Text>
        <Text style={[styles.infoText, styles.bold]}>{correo}</Text>
        <Text style={styles.infoText}>Rol:</Text>
        <Text style={[styles.infoText, styles.bold]}>{rol}</Text>
        <Text style={styles.infoText}>Carrera:</Text>
        <Text style={[styles.infoText, styles.bold]}>{carrera}</Text>
        <Text style={styles.infoText}>Semestre:</Text>
        <Text style={[styles.infoText, styles.bold]}>{semestre}</Text>
        <Text style={styles.infoText}>Numero de Control:</Text>
        <Text style={[styles.infoText, styles.bold]}>{numeroControl}</Text>
        <Text style={styles.infoText}>Turno:</Text>
        <Text style={[styles.infoText, styles.bold]}>{turno}</Text>
      </View>

      <TouchableOpacity style={styles.editButton} onPress={() => navigation.navigate('EditInfo')}>
        <Text style={styles.editButtonText}>Editar informaci√≥n</Text>
      </TouchableOpacity>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  userContainer: {
    marginTop: 50,
    marginBottom: 30,
    alignItems: 'center',
    justifyContent: 'center',
  },
  userIcon: {
    width: 120,
    height: 120,
    resizeMode: 'contain',
  },
  infoContainer: {
    marginBottom: 30,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  infoText: {
    fontSize: 18,
    marginVertical: 5,
  },
  bold: {
    fontWeight: 'bold',
  },
  editButton: {
    backgroundColor: '#0069c0',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  editButtonText: {
    color: '#fff',
    fontSize: 16,
  },
});

export default Profile;
