import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import { getAuth } from 'firebase/auth';
import { getFirestore, doc, getDoc } from 'firebase/firestore';
import { firebaseApp } from '../firebase/credenciales';
import { initializeApp } from 'firebase/app';
import {firebaseConfig} from '../firebase/credenciales';

const Profile = () => {
  const [user, setUser] = useState(null);
  const firebaseApp = initializeApp(firebaseConfig);
  const auth = getAuth(firebaseApp);

  useEffect(() => {
    const fetchData = async () => {
      const auth = getAuth(firebaseApp);
      const currentUser = auth.currentUser;

      if (currentUser) {
        const db = getFirestore();
        const userRef = doc(db, 'usuarios', currentUser.uid);
        const userDoc = await getDoc(userRef);

        if (userDoc.exists()) {
          setUser(userDoc.data());
        }
      }
    };

    fetchData();
  }, []);

  if (!user) {
    return (
      <View>
        <Text>Loading...</Text>
      </View>
    );
  }

  return (
    <View>
      <Text>Nombre: {user.nombre}</Text>
      <Text>Correo: {user.correo}</Text>
      <Text>Rol: {user.rol}</Text>
      <Text>Carrera: {user.carrera}</Text>
      <Text>Semestre: {user.semestre}</Text>
      <Text>NÃºmero de Control: {user.numeroControl}</Text>
      <Text>Turno: {user.turno}</Text>
    </View>
  );
};

export default Profile;
