import React, { useState } from 'react';
import { View, Text, StatusBar, TextInput, TouchableOpacity, StyleSheet, Picker } from 'react-native';

const EditarInformacion = ({ navigation }) => {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [rol, setRol] = useState('');

  const handleNombreChange = (value) => {
    setNombre(value);
  }

  const handleCorreoChange = (value) => {
    setCorreo(value);
  }

  const handleRolChange = (value) => {
    setRol(value);
  }

  const handleConfirmarClick = () => {
    // Lógica para confirmar la edición de la información del perfil
  }

  return (
    <View style={styles.container}>
      <View style={styles.buttonsContainer}>
      <StatusBar backgroundColor="#007AFF" />
      <Text style={styles.title}>Editar Información</Text>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Nombre</Text>
        <TextInput
          style={styles.input}
          placeholder="Ingresa tu nombre completo"
          value={nombre}
          onChangeText={handleNombreChange}
        />
      </View>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Correo</Text>
        <TextInput
          style={styles.input}
          placeholder="Ingresa tu correo electrónico"
          value={correo}
          onChangeText={handleCorreoChange}
        />
      </View>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Rol</Text>
        <Picker
          style={styles.input}
          selectedValue={rol}
          onValueChange={handleRolChange}>
          <Picker.Item label="Selecciona un rol" value="" />
          <Picker.Item label="Administrador" value="Administrador" />
          <Picker.Item label="Estudiante" value="Estudiante" />
          <Picker.Item label="Profesor" value="Profesor" />
          <Picker.Item label="Coordinación" value="Coordinación" />
        </Picker>
      </View>
      <TouchableOpacity style={styles.button} onPress={handleConfirmarClick}>
        <Text style={styles.buttonText}>Confirmar</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Mi Perfil')}>
        <Text style={styles.buttonText}>Regresa</Text>
      </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#FFFFFF',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    textAlign: 'center',
    marginTop: 20,
    marginBottom: 20,
  },
  buttonsContainer: {
    width: '60%', // Establecer el ancho del contenedor de los botones al 60% de la pantalla
    alignItems: 'flex-start', // Alinear los botones a la izquierda
  },
  inputContainer: {
    marginHorizontal: 20,
    marginTop: 10,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
    color: '#333333',
  },
  input: {
    height: 40,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: '#CCCCCC',
    paddingLeft: 10,
  },
  button: {
    backgroundColor: '#007AFF',
    padding: 16,
    borderRadius: 8,
    marginBottom: 16,
    width: '100%', // Establecer el ancho de cada botón al 100% del ancho del contenedor
    flexDirection: 'row', // Alinear el icono y el texto horizontalmente
    alignItems: 'center', // Alinear el icono y el texto verticalmente
  },
  buttonText: {
    color: '#FFFFFF',
    fontWeight: 'bold',
    fontSize: 16,
  },
});


export default EditarInformacion;
