import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View, StatusBar, TouchableOpacity, Image } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import firestore from '@react-native-firebase/firestore';

const Profile = () => {
  const navigation = useNavigation();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Fetch user data from Firestore and update the state
    const fetchUserData = async () => {
      try {
        const userDoc = await firestore().collection('users').doc('yourUserID').get();
        const userData = userDoc.data();
        setUserData(userData);
      } catch (error) {
        console.log('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <View style={styles.container}>
      <StatusBar backgroundColor="#0069c0" barStyle="light-content" />

      <View style={styles.userContainer}>
        <Image source={require('../images/usuario.png')} style={styles.userIcon} />
      </View>

      <View style={styles.infoContainer}>
        <Text style={styles.infoText}>Nombre:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.nombre}</Text>}
        <Text style={styles.infoText}>Correo:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.correo}</Text>}
        <Text style={styles.infoText}>Rol:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.rol}</Text>}
        <Text style={styles.infoText}>Carrera:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.carrera}</Text>}
        <Text style={styles.infoText}>Semestre:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.semestre}</Text>}
        <Text style={styles.infoText}>Numero de Control:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.numeroControl}</Text>}
        <Text style={styles.infoText}>Turno:</Text>
        {userData && <Text style={[styles.infoText, styles.bold]}>{userData.turno}</Text>}
      </View>

      <TouchableOpacity style={styles.editButton} onPress={() => navigation.navigate('EditInfo')}>
        <Text style={styles.editButtonText}>Editar informaci√≥n</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  userContainer: {
    marginTop: 50,
    marginBottom: 30,
    alignItems: 'center',
    justifyContent: 'center',
  },
  userIcon: {
    width: 120,
    height: 120,
    resizeMode: 'contain',
  },
  infoContainer: {
    marginBottom: 30,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  infoText: {
    fontSize: 18,
    marginVertical: 5,
  },
  bold: {
    fontWeight: 'bold',
  },
  editButton: {
    backgroundColor: '#0069c0',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  editButtonText: {
    color: '#fff',
    fontSize: 16,
  },
});

export default Profile;
