import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Divider } from 'react-native';

const SettingsScreen = () => {
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.button}>
        <Text style={styles.buttonText}>Configuraci√≥n</Text>
      </TouchableOpacity>
      <Divider style={styles.divider} />
      <TouchableOpacity style={styles.button}>
        <Text style={styles.buttonText}>Tema</Text>
      </TouchableOpacity>
      <Divider style={styles.divider} />
      <TouchableOpacity style={styles.button}>
        <Text style={styles.buttonText}>Seguridad y privacidad</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#fff',
    padding: 16,
    borderRadius: 8,
    marginBottom: 16,
  },
  buttonText: {
    color: '#333',
    fontWeight: 'bold',
    fontSize: 18,
  },
  divider: {
    height: 1,
    backgroundColor: '#333',
    width: '100%',
    marginVertical: 16,
  },
});

export default SettingsScreen;
